#include <iostream>#include <memory>struct Rat {    int length;    Rat(int value) : length(value) {        std::cout << "Rat born" << std::endl;    }    ~Rat() {        std::cout << "Rat destroyed :(" << std::endl;    }};int main() {		std::shared_ptr<Rat> rat1 = std::make_shared<Rat>(10);        {			std::shared_ptr<Rat> rat2 = rat1;            std::cout << rat1.use_count() << " rat(s) alive" << std::endl; //RATS POPULATION GROWTHS        }		std::cout << rat1.use_count() << " rat(s) alive" << std::endl;    return 0;}//When we left the scope, second ptr to RAT destructs//When we left the main, destructs first ptr to RAT and RAT object itself